const $ = django.jQuery;

function editUp(row) {

    row_parent_id = row[0].parentElement.parentElement.parentElement.parentElement.id
    if (row_parent_id == "izd_form")
    {

        up_num = row[0].id.split("-")[1];

        $("#id_up_set-" + up_num + "-up_num").val(up_num*1 + 1);
        $("#id_up_set-" + up_num + "-izd_title").val($("#id_izd_title").val());
        $("#id_up_set-" + up_num + "-izd_name1").val($("#id_izd_name1").val());
        $("#id_up_set-" + up_num + "-izd_name2").val($("#id_izd_name2").val());






    }

    return



}