const $ = django.jQuery;

function init_row0() {
    $("#id_up_set-" + 0 + "-up_num").val(1);
    $("#id_up_set-" + 0 + "-izd_title").val($("#id_izd_title").val());
    $("#id_up_set-" + 0 + "-izd_name1").val($("#id_izd_name1").val());
    $("#id_up_set-" + 0 + "-izd_name2").val($("#id_izd_name2").val());

}
function editUp(row) {

    row_parent_id = row[0].parentElement.parentElement.parentElement.parentElement.id
    if (row_parent_id == "izd_form")
    {

        row_num = row[0].id.split("-")[1] * 1;
        up_num = row_num + 1;

      //  $("#id_up_set-" + row_num + "-up_num").val(up_num);
        $("#id_up_set-" + row_num + "-izd_title").val($("#id_izd_title").val());
        $("#id_up_set-" + row_num + "-izd_name1").val($("#id_izd_name1").val());
        $("#id_up_set-" + row_num + "-izd_name2").val($("#id_izd_name2").val());

        $("#id_up_set-" + row_num + "-up_type").val($("#id_up_set-" + 0 + "-up_type").val())

        tcolor = $("#id_up_set-" + 0 + "-up_color")[0];
        for ( i = 0; i < tcolor.length; i++ )
        {
            $("#id_up_set-" + row_num + "-up_color")[0][i].selected = tcolor[i].selected;
        }

        tgroupcolor = $("#id_up_set-" + 0 + "-up_color_group")[0];
        for ( i = 0; i < tgroupcolor.length; i++ )
        {
            $("#id_up_set-" + row_num + "-up_color_group")[0][i].selected = tgroupcolor[i].selected;
        }

    }

    return



}